INSERT INTO public.pattern VALUES ('1a1b9319-e303-431d-91c3-f4a3b6189982', 'Pipes and Filters', 'https://patternpedia.org/patternLanguages/eip/pipesAndFilters', '{"Icon": " ![](https://www.enterpriseintegrationpatterns.com/img/PipesAndFiltersIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/PipesAndFilters.gif)", "Solution": "Use the Pipes and Filters architectural style to divide a larger processing task into a sequence of smaller, independent processing steps (Filters) that are connected by channels (Pipes).", "Driving Question": "How can we perform complex processing on a message while maintaining independence and flexibility?"}', 'https://www.enterpriseintegrationpatterns.com/img/PipesAndFiltersIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('9bafcace-20a3-4726-963b-06d09437c677', 'Message Router', 'https://patternpedia.org/patternLanguages/eip/messageRouter', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/ContentBasedRouterIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/MessageRouter.gif)", "Solution": "Insert a special filter, a Message Router, which consumes a Message from one Message Channel and republishes it to a different Message Channel channel depending on a set of conditions.", "Driving Question": "How can you decouple individual processing steps so that messages can be passed to different filters depending on a set of conditions?"}', 'https://www.enterpriseintegrationpatterns.com/img/ContentBasedRouterIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('26c8f1cb-a592-415e-846e-22f2ee761c8f', 'Message Store', 'https://patternpedia.org/patternLanguages/eip/messageStore', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/MessageStoreIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/MessageStore.gif)", "Solution": "Use a Message Store to capture information about each message in a central location.", "Driving Question": "How can we report against message information without disturbing the loosely coupled and transient nature of a messaging system?"}', 'https://www.enterpriseintegrationpatterns.com/img/MessageStoreIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('d89d5a40-b864-4d97-9b44-211ed25c4f98', 'Content-Based Router', 'https://patternpedia.org/patternLanguages/eip/content-basedRouter', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/ContentBasedRouterIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/ContentBasedRouter.gif)", "Solution": "Use a Content-Based Router to route each message to the correct recipient based on message content.", "Driving Question": "How do we handle a situation where the implementation of a single logical function (e.g., inventory check) is spread across multiple physical systems?"}', 'https://www.enterpriseintegrationpatterns.com/img/ContentBasedRouterIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('c56361d3-4230-4653-942b-fc8ac7488bf9', 'Message', 'https://patternpedia.org/patternLanguages/eip/message', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/MessageIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/MessageSolution.gif)", "Solution": "Package the information into a Message, a data record that the messaging system can transmit through a message channel.", "Driving Question": "How can two applications connected by a message channel exchange a piece of information?"}', 'https://www.enterpriseintegrationpatterns.com/img/MessageIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('19c5ed05-1a24-4971-9aac-390e1931f010', 'Command Message', 'https://patternpedia.org/patternLanguages/eip/commandMessage', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/CommandMessageIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/CommandMessageSolution.gif)", "Solution": "Use a Command Message to reliably invoke a procedure in another application.", "Driving Question": "How can messaging be used to invoke a procedure in another application?"}', 'https://www.enterpriseintegrationpatterns.com/img/CommandMessageIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('b14e9775-09ad-4019-b5db-dd9670221679', 'Document Message', 'https://patternpedia.org/patternLanguages/eip/documentMessage', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/DocumentMessageIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/DocumentMessageSolution.gif)", "Solution": "Use a Document Message to reliably transfer a data structure between applications.", "Driving Question": "How can messaging be used to transfer data between applications?"}', 'https://www.enterpriseintegrationpatterns.com/img/DocumentMessageIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('f3afc386-7c81-41d1-9b94-a06a49deb965', 'Event Message', 'https://patternpedia.org/patternLanguages/eip/eventMessage', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/EventMessageIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/EventMessageSolution.gif)", "Solution": "Use an Event Message for reliable, asynchronous event notification between applications.", "Driving Question": "How can messaging be used to transmit events from one application to another?"}', 'https://www.enterpriseintegrationpatterns.com/img/EventMessageIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('ef45bb4d-7f1a-4840-9783-aa22ed40032a', 'Request-Reply', 'https://patternpedia.org/patternLanguages/eip/request-reply', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/RequestReplyIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/RequestReply.gif)", "Solution": "Send a pair of Request-Reply messages, each on its own channel.", "Driving Question": "When an application sends a message, how can it get a response from the receiver?"}', 'https://www.enterpriseintegrationpatterns.com/img/RequestReplyIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('ea7c0692-6545-40e3-8cf8-f4c828c5fce8', 'Return Address', 'https://patternpedia.org/patternLanguages/eip/returnAddress', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/ReturnAddressIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/ReturnAddressSolution.gif)", "Solution": "The request message should contain a Return Address that indicates where to send the reply message.", "Driving Question": "How does a replier know where to send the reply?"}', 'https://www.enterpriseintegrationpatterns.com/img/ReturnAddressIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('330ba4a6-411d-4d44-913a-2805a9f21cf7', 'Correlation Identifier', 'https://patternpedia.org/patternLanguages/eip/correlationIdentifier', '{"Icon": " ![](https://www.enterpriseintegrationpatterns.com/img/CorrelationIdentifierIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/CorrelationIdentifierSolution.gif)", "Solution": "Each reply message should contain a Correlation Identifier, a unique identifier that indicates which request message this reply is for.", "Driving Question": "How does a requestor that has received a reply know which request this is the reply for?"}', 'https://www.enterpriseintegrationpatterns.com/img/CorrelationIdentifierIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('acb053d6-b8be-4bce-b62d-3a8aa10b9a0d', 'Message Sequence', 'https://patternpedia.org/patternLanguages/eip/messageSequence', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/MessageSequenceIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/MessageSequenceSize.gif)", "Solution": "Whenever a large set of data may need to be broken into message-size chunks, send the data as a Message Sequence and mark each message with sequence identification fields.", "Driving Question": "How can messaging transmit an arbitrarily large amount of data?"}', 'https://www.enterpriseintegrationpatterns.com/img/MessageSequenceIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('c34d4c11-da4f-445d-8682-1c92dcfbb6be', 'Message Expiration', 'https://patternpedia.org/patternLanguages/eip/messageExpiration', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/MessageExpirationIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/MessageExpirationSolution.gif)", "Solution": "Set the Message Expiration to specify a time limit how long the message is viable.", "Driving Question": "How can a sender indicate when a message should be considered stale and thus shouldnt be processed?"}', 'https://www.enterpriseintegrationpatterns.com/img/MessageExpirationIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('01d63259-109f-4136-878d-77e503ac8a11', 'Format Indicator', 'https://patternpedia.org/patternLanguages/eip/formatIndicator', '{"Icon": "", "Sketch": "", "Solution": "Design a data format that includes a Format Indicator, so that the message specifies what format it is using.", "Driving Question": "How can a messages data format be designed to allow for possible future changes?"}', '', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('9c49472d-47fc-417e-b75a-512d57e76870', 'Message Filter', 'https://patternpedia.org/patternLanguages/eip/messageFilter', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/MessageFilterIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/MessageFilter.gif)", "Solution": "Use a special kind of Message Router, a Message Filter, to eliminate undesired messages from a channel based on a set of criteria.", "Driving Question": "How can a component avoid receiving uninteresting messages?"}', 'https://www.enterpriseintegrationpatterns.com/img/MessageFilterIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('be9c4245-79db-40cd-a0d4-c750c3a822b6', 'Dynamic Router', 'https://patternpedia.org/patternLanguages/eip/dynamicRouter', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/DynamicRouterIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/DynamicRouter.gif)", "Solution": "Use a Dynamic Router, a Router that can self-configure based on special configuration messages from participating destinations.", "Driving Question": "How can you avoid the dependency of the router on all possible destinations while maintaining its efficiency?"}', 'https://www.enterpriseintegrationpatterns.com/img/DynamicRouterIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('0b8fa48f-6ccf-48ef-ac0b-3dadadad40bf', 'Recipient List', 'https://patternpedia.org/patternLanguages/eip/recipientList', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/RecipientListIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/RecipientList.gif)", "Solution": "Define a channel for each recipient. Then use a Recipient List to inspect an incoming message, determine the list of desired recipients, and forward the message to all channels associated with the recipients in the list.", "Driving Question": "How do we route a message to a list of dynamically specified recipients?"}', 'https://www.enterpriseintegrationpatterns.com/img/RecipientListIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('7d28fe21-466a-4e22-9851-374e9cb93cd9', 'Splitter', 'https://patternpedia.org/patternLanguages/eip/splitter', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/SplitterIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/Sequencer.gif) ", "Solution": "Use a Splitter to break out the composite message into a series of individual messages, each containing data related to one item.", "Driving Question": "How can we process a message if it contains multiple elements, each of which may have to be processed in a different way?"}', 'https://www.enterpriseintegrationpatterns.com/img/SplitterIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('db5033cf-c13f-47b1-a617-aea77a0d5864', 'Aggregator', 'https://patternpedia.org/patternLanguages/eip/aggregator', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/AggregatorIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/Aggregator.gif)", "Solution": "Use a stateful filter, an Aggregator, to collect and store individual messages until a complete set of related messages has been received. Then, the Aggregator publishes a single message distilled from the individual messages.", "Driving Question": "How do we combine the results of individual, but related messages so that they can be processed as a whole?"}', 'https://www.enterpriseintegrationpatterns.com/img/AggregatorIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('8c047398-63a1-4940-a5c5-c44834f48516', 'Resequencer', 'https://patternpedia.org/patternLanguages/eip/resequencer', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/ResequencerIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/Resequencer.gif)", "Solution": "Use a stateful filter, a Resequencer, to collect and re-order messages so that they can be published to the output channel in a specified order.", "Driving Question": "How can we get a stream of related but out-of-sequence messages back into the correct order?"}', 'https://www.enterpriseintegrationpatterns.com/img/ResequencerIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('885ade35-d310-421a-98b8-877e27e51531', 'Composed Message Processor', 'https://patternpedia.org/patternLanguages/eip/composedMessageProcessor', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/DistributionAggregateIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/DistributionAggregate.gif)", "Solution": "Use Composed Message Processor to process a composite message. The Composed Message Processor splits the message up, routes the sub-messages to the appropriate destinations and re-aggregates the responses back into a single message.", "Driving Question": "How can you maintain the overall message flow when processing a message consisting of multiple elements, each of which may require different processing?"}', 'https://www.enterpriseintegrationpatterns.com/img/DistributionAggregateIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('ba3aaa33-9010-459f-acb5-5452788e9abc', 'Scatter-Gather', 'https://patternpedia.org/patternLanguages/eip/scatter-gather', '{"Icon": "", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/BroadcastAggregate.gif)", "Solution": "Use a Scatter-Gather that broadcasts a message to multiple recipients and re-aggregates the responses back into a single message.", "Driving Question": "How do you maintain the overall message flow when a message needs to be sent to multiple recipients, each of which may send a reply?"}', '', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('c7e15bdd-a5f3-486d-8e3d-f3117f39eb1a', 'Routing Slip', 'https://patternpedia.org/patternLanguages/eip/routingSlip', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/RoutingTableIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/RoutingTableSimple.gif)", "Solution": "Attach a Routing Slip to each message, specifying the sequence of processing steps. Wrap each component with a special message router that reads the Routing Slip and routes the message to the next component in the list.", "Driving Question": "How do we route a message consecutively through a series of processing steps when the sequence of steps is not known at design-time and may vary for each message?"}', 'https://www.enterpriseintegrationpatterns.com/img/RoutingTableIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('53adbbff-f4d6-4bd7-8b90-475f57913c1a', 'Process Manager', 'https://patternpedia.org/patternLanguages/eip/processManager', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/ProcessManagerIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/ProcessManager.gif)", "Solution": "Use a central processing unit, a Process Manager, to maintain the state of the sequence and determine the next processing step based on intermediate results.", "Driving Question": "How do we route a message through multiple processing steps when the required steps may not be known at design-time and may not be sequential?"}', 'https://www.enterpriseintegrationpatterns.com/img/ProcessManagerIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('329b49fc-de79-4d63-97ca-87a8f83ef1e2', 'Message Broker', 'https://patternpedia.org/patternLanguages/eip/messageBroker', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/MessageBrokerIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/MessageBroker.gif)", "Solution": "Use a central Message Broker that can receive messages from multiple destinations, determine the correct destination and route the message to the correct channel. Implement the internals of the Message Broker using the design patterns presented in this chapter.", "Driving Question": "How can you decouple the destination of a message from the sender and maintain central control over the flow of messages?"}', 'https://www.enterpriseintegrationpatterns.com/img/MessageBrokerIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('29f4224d-6b91-4218-aed6-0db3e5fa9dc2', 'Message Translator', 'https://patternpedia.org/patternLanguages/eip/messageTranslator', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/MessageTranslatorIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/MessageTranslator.gif)", "Solution": "Use a special filter, a Message Translator, between other filters or applications to translate one data format into another.", "Driving Question": "How can systems using different data formats communicate with each other using messaging?"}', 'https://www.enterpriseintegrationpatterns.com/img/MessageTranslatorIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('658b6fce-40b0-4f05-a2da-2fd3e33684fb', 'Envelope Wrapper', 'https://patternpedia.org/patternLanguages/eip/envelopeWrapper', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/EnvelopeWrapperIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/Wrapper.gif)", "Solution": "Use a Envelope Wrapper to wrap application data inside an envelope that is compliant with the messaging infrastructure. Unwrap the message when it arrives at the destination.", "Driving Question": "How can existing systems participate in a messaging exchange that places specific requirements on the message format, such as message header fields or encryption?"}', 'https://www.enterpriseintegrationpatterns.com/img/EnvelopeWrapperIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('b3418862-26a6-4bbb-b546-c12f48c45b16', 'Content Enricher', 'https://patternpedia.org/patternLanguages/eip/contentEnricher', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/DataEnricherIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/DataEnricher.gif)", "Solution": "Use a specialized transformer, a Content Enricher, to access an external data source in order to augment a message with missing information.", "Driving Question": "How do we communicate with another system if the message originator does not have all the required data items available?"}', 'https://www.enterpriseintegrationpatterns.com/img/DataEnricherIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('26bc2583-1c9f-4d6e-9968-e5aa0e8ab82f', 'Content Filter', 'https://patternpedia.org/patternLanguages/eip/contentFilter', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/ContentFilterIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/ContentFilter.gif)", "Solution": "Use a Content Filter to remove unimportant data items from a message leaving only important items.", "Driving Question": "How do you simplify dealing with a large message, when you are interested only in a few data items?"}', 'https://www.enterpriseintegrationpatterns.com/img/ContentFilterIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('6c3f4121-65c2-444e-9d5a-f16f47fd2ed5', 'Claim Check', 'https://patternpedia.org/patternLanguages/eip/claimCheck', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/StoreInLibraryIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/StoreInLibrary.gif)", "Solution": "Store message data in a persistent store and pass a Claim Check to subsequent components. These components can use the Claim Check to retrieve the stored information.", "Driving Question": "How can we reduce the data volume of message sent across the system without sacrificing information content?"}', 'https://www.enterpriseintegrationpatterns.com/img/StoreInLibraryIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('89dd6672-dd42-470b-8e84-129e4ec7540c', 'Normalizer', 'https://patternpedia.org/patternLanguages/eip/normalizer', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/NormalizerIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/NormalizerDetail.gif)", "Solution": "Use a Normalizer to route each message type through a custom Message Translator so that the resulting messages match a common format.", "Driving Question": "How do you process messages that are semantically equivalent, but arrive in a different format?"}', 'https://www.enterpriseintegrationpatterns.com/img/NormalizerIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('98ccd4dc-86a8-4652-b2c1-5b83b6f078e1', 'Canonical Data Model', 'https://patternpedia.org/patternLanguages/eip/canonicalDataModel', '{"Icon": "", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/CanonicalDataModel.gif)", "Solution": "Therefore, design a Canonical Data Model that is independent from any specific application. Require each application to produce and consume messages in this common format.", "Driving Question": "How can you minimize dependencies when integrating applications that use different data formats?"}', '', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('3953e924-30f6-4561-8e02-e761b417c1d4', 'Message Endpoint', 'https://patternpedia.org/patternLanguages/eip/messageEndpoint', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/MessageEndpointIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/MessageEndpointSolution.gif)", "Solution": "Connect an application to a messaging channel using a Message Endpoint, a client of the messaging system that the application can then use to send or receive messages.", "Driving Question": "How does an application connect to a messaging channel to send and receive messages?"}', 'https://www.enterpriseintegrationpatterns.com/img/MessageEndpointIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('02fc1261-b7d6-4d85-89ef-c987ab92f3d4', 'Messaging Gateway', 'https://patternpedia.org/patternLanguages/eip/messagingGateway', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/MessagingGatewayIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/MessagingGatewaySolution.gif)", "Solution": "Use a Messaging Gateway, a class than wraps messaging-specific method calls and exposes domain-specific methods to the application.", "Driving Question": "How do you encapsulate access to the messaging system from the rest of the application?"}', 'https://www.enterpriseintegrationpatterns.com/img/MessagingGatewayIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('29acda13-a41f-4270-9248-375814889f20', 'Messaging Mapper', 'https://patternpedia.org/patternLanguages/eip/messagingMapper', '{"Icon": "", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/MessagingMapperClassDiagram.gif)", "Solution": "Create a separate Messaging Mapper that contains the mapping logic between the messaging infrastructure and the domain objects. Neither the objects nor the infrastructure have knowledge of the Messaging Mapper''s existence.", "Driving Question": "How do you move data between domain objects and the messaging infrastructure while keeping the two independent of each other?"}', '', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('a2b0eaca-aa24-4036-b730-265a97043b75', 'Transactional Client', 'https://patternpedia.org/patternLanguages/eip/transactionalClient', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/TransactionalClientIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/TransactionalClientSolution.gif)", "Solution": "Use a Transactional Clientmake the clients session with the messaging system transactional so that the client can specify transaction boundaries.", "Driving Question": "How can a client control its transactions with the messaging system?"}', 'https://www.enterpriseintegrationpatterns.com/img/TransactionalClientIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('78b58f38-9075-44a1-8eb4-5ab0ba4b801d', 'Polling Consumer', 'https://patternpedia.org/patternLanguages/eip/pollingConsumer', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/PollingConsumerIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/PollingConsumerSolution.gif)", "Solution": "The application should use a Polling Consumer, one that explicitly makes a call when it wants to receive a message.", "Driving Question": "How can an application consume a message when the application is ready?"}', 'https://www.enterpriseintegrationpatterns.com/img/PollingConsumerIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('d9bd086d-030c-4ca4-b6d2-29c2fbf517c8', 'Competing Consumers', 'https://patternpedia.org/patternLanguages/eip/competingConsumers', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/CompetingConsumersIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/CompetingConsumers.gif)", "Solution": "Create multiple Competing Consumers on a single channel so that the consumers can process multiple messages concurrently.", "Driving Question": "How can a messaging client process multiple messages concurrently?"}', 'https://www.enterpriseintegrationpatterns.com/img/CompetingConsumersIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('7c775873-1ebf-4574-8cb1-29b0c661b8f3', 'Message Dispatcher', 'https://patternpedia.org/patternLanguages/eip/messageDispatcher', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/MessageDispatcherIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/MessageDispatcher.gif)", "Solution": "Create a Message Dispatcher on a channel that will consume messages from a channel and distribute them to performers.", "Driving Question": "How can multiple consumers on a single channel coordinate their message processing?"}', 'https://www.enterpriseintegrationpatterns.com/img/MessageDispatcherIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('0ee54310-5f4d-4435-9c34-cf93f742e101', 'Selective Consumer', 'https://patternpedia.org/patternLanguages/eip/selectiveConsumer', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/MessageSelectorIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/MessageSelectorSolution.gif)", "Solution": "Make the consumer a Selective Consumer, one that filteres the messages delivered by its channel so that it only receives the ones that match its criteria.", "Driving Question": "How can a message consumer select which messages it wishes to receive?"}', 'https://www.enterpriseintegrationpatterns.com/img/MessageSelectorIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('ca07715c-696e-4a62-af10-74145ff818f5', 'Durable Subscriber', 'https://patternpedia.org/patternLanguages/eip/durableSubscriber', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/DurableSubscriptionIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/DurableSubscriptionSolution.gif)", "Solution": "Use a Durable Subscriber to make the messaging system save messages published while the subscriber is disconnected.", "Driving Question": "How can a subscriber avoid missing messages while its not listening for them?"}', 'https://www.enterpriseintegrationpatterns.com/img/DurableSubscriptionIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('06c5d5cc-5c29-4a74-be75-dfba20cc4f80', 'Idempotent Receiver', 'https://patternpedia.org/patternLanguages/eip/idempotentReceiver', '{"Icon": "", "Sketch": "", "Solution": "Design a receiver to be an Idempotent Receiver--one that can safely receive the same message multiple times.", "Driving Question": "How can a message receiver deal with duplicate messages?"}', '', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('9167d0da-69ec-40a5-b7f8-5eab25833d70', 'Service Activator', 'https://patternpedia.org/patternLanguages/eip/serviceActivator', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/MessagingAdapterIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/MessagingAdapterSolution.gif)", "Solution": "Design a Service Activator that connects the messages on the channel to the service being accessed.", "Driving Question": "How can an application design a service to be invoked both via various messaging technologies and via non-messaging techniques?"}', 'https://www.enterpriseintegrationpatterns.com/img/MessagingAdapterIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('3b3a9b48-63aa-4523-be3a-6a39fdf4bda7', 'Point-to-Point Channel', 'https://patternpedia.org/patternLanguages/eip/point-to-pointChannel', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/PointToPointIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/PointToPointSolution.gif)", "Solution": "Send the message on a Point-to-Point Channel, which ensures that only one receiver will receive a particular message.", "Driving Question": "How can the caller be sure that exactly one receiver will receive the document or perform the call?"}', 'https://www.enterpriseintegrationpatterns.com/img/PointToPointIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('28343b10-ce82-4c1f-aef2-f7d95316b48e', 'Publish-Subscribe Channel', 'https://patternpedia.org/patternLanguages/eip/publish-subscribeChannel', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/PublishSubscribeIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/PublishSubscribeSolution.gif)", "Solution": "Send the event on a Publish-Subscribe Channel, which delivers a copy of a particular event to each receiver.", "Driving Question": "How can the sender broadcast an event to all interested receivers?"}', 'https://www.enterpriseintegrationpatterns.com/img/PublishSubscribeIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('cf3a2b5f-9d7a-4f2c-ba39-ab2911d3a7d7', 'Datatype Channel', 'https://patternpedia.org/patternLanguages/eip/datatypeChannel', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/DatatypeChannelIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/DatatypeSolution.gif)", "Solution": "Use a separate Datatype Channel for each data type, so that all data on a particular channel is of the same type.", "Driving Question": "How can the application send a data item such that the receiver will know how to process it?"}', 'https://www.enterpriseintegrationpatterns.com/img/DatatypeChannelIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('2229a430-fe92-4411-9d72-d10dd1d8da14', 'Uniform Superposition', 'https://patternpedia.org/patternLanguages/reformulatedQuantumComputingPatterns/uniformSuperposition', '{"Alias": "", "Intent": "Create a uniform superposition of all possible states of a quantum register", "Result": "A [Uniform Superposition](pattern-languages/af7780d5-1f97-4536-8da7-4194b093ab1d/2229a430-fe92-4411-9d72-d10dd1d8da14) can be achieved by performing a single parallel operation on each qubit, increasing the depth of the entire circuit by only one.\n\n", "Context": "The power of quantum algorithms partially originates in the ability to represent multiple states at once, the so-called quantum parallelism.  \nTo realize quantum parallelism, some qubits of a quantum register must be brought into superposition.  \nMany algorithms start with obtaining an equally weighted superposition in (a part of) a quantum register. \nTherefore, for every state represented by (this part of) the quantum register,  the measurement probability is the same.", "Solution": "After initializing the quantum register as the unit vector $\\left|0 \\ldots 0\\right>$, a Uniform Superposition is created via the Hadamard transformation:\n\n\n$$\n\\newcommand{\\colVec}[1]{% inline column vector\n\\bigl( \\begin{smallmatrix}#1\\end{smallmatrix}\\bigr)\n}\n\\newcommand{\\bigColVec}[1]{% inline column vector\n\\left( \\begin{matrix}#1\\end{matrix}\\right)\n}\n\\newcommand{\\state}[1]{{\\left| #1 \\right>}}H^{\\otimes n} \\left( \\left| 0 \\right>^{\\otimes n} \\right)= \\dfrac{1}{\\sqrt{2^n}} \\sum_{x=0}^{2^n-1} \\left|x \\right>$$\nThe quantum register may also include ancilla bits that must not necessarily be brought into superposition.\nFor example, if the last part of the quantum register is used for ancilla qubits, a different [Initialization](pattern-languages/af7780d5-1f97-4536-8da7-4194b093ab1d/312bc9d3-26c0-40ae-b90b-56effd136c0d) on the two parts of the register can be accomplished by using a tensor product operator $H^{\\otimes n}\\otimes U$ where the Hadamard transformation $H^{\\otimes n}$ only operates on the first part and $U$ operates on the ancilla qubits. If the ancilla qubits are not brought into superposition, $U$ is the Identity (e.g., $U=I^{\\otimes m}$):\n$$ \n\\newcommand{\\state}[1]{{\\left| #1 \\right>}}\nH^{\\otimes n} \\otimes I^{\\otimes m} \\left( \\left| 0 \\right>^{\\otimes n} \\otimes \\left| 0 \\right>^{\\otimes m} \\right)= \\frac{1}{\\sqrt{2^n}} \\sum_{x=0}^{2^n-1} \\left|x \\right> \\otimes \\state{0}^{\\otimes m}\n$$", "Variants": "", "Known Uses": "Countless quantum algorithms start with a  uniform superposition.\n\n[Grover''s Algorithm (PlanQK)](https://platform.planqk.de/algorithms/86dfd279-db46-4adb-84a7-39bcf1d19d3a/)\n[Drr-Hyer Quantum Minimization Algorithm (PlanQK)](https://platform.planqk.de/algorithms/061f4eb3-d9ee-4f47-befc-9e242bf801ce/)\n[Quantum Phase Estimation (PlanQK)](https://platform.planqk.de/algorithms/ba4c50e3-7429-4471-a250-1762ed4c556a/)\n[Quantum approximate optimization algorithm (PlanQK)](https://platform.planqk.de/algorithms/fae60bca-d2b6-4aa2-88b7-58caace34179/)\n\n", "Related Patterns": "[Uniform Superposition](pattern-languages/af7780d5-1f97-4536-8da7-4194b093ab1d/2229a430-fe92-4411-9d72-d10dd1d8da14) is a special case of [Initialization](pattern-languages/af7780d5-1f97-4536-8da7-4194b093ab1d/312bc9d3-26c0-40ae-b90b-56effd136c0d).  \nAfter a register is brought into uniform superposition, [Creating Entanglement](pattern-languages/af7780d5-1f97-4536-8da7-4194b093ab1d/3d1f3991-df47-4d42-8f9a-e6dcf4e3ccec) may be used. \nA [Function Table](pattern-languages/af7780d5-1f97-4536-8da7-4194b093ab1d/3f3fabf0-7fa7-4b43-a74a-46a7ac2c55ee)can be computed on a register in uniform superposition.\nAn [Oracle](pattern-languages/af7780d5-1f97-4536-8da7-4194b093ab1d/1cc7e9d6-ab37-412e-8afa-604a25de296e) may  require its input to be in uniform superposition."}', 'https://quantumcomputingpatterns.org/assets/pattern-icons/equal_superposition_icon.png', '{"Alias": "", "Intent": "Create a uniform superposition of all possible states of a quantum register", "Result": "A [Uniform Superposition](pattern-languages/af7780d5-1f97-4536-8da7-4194b093ab1d/2229a430-fe92-4411-9d72-d10dd1d8da14) can be achieved by performing a single parallel operation on each qubit, increasing the depth of the entire circuit by only one.\n\n", "Context": "The power of quantum algorithms partially originates in the ability to represent multiple states at once, the so-called quantum parallelism.  \nTo realize quantum parallelism, some qubits of a quantum register must be brought into superposition.  \nMany algorithms start with obtaining an equally weighted superposition in (a part of) a quantum register. \nTherefore, for every state represented by (this part of) the quantum register,  the measurement probability is the same.", "Solution": "After initializing the quantum register as the unit vector $\\left|0 \\ldots 0\\right>$, a Uniform Superposition is created via the Hadamard transformation:\n\n\n$$\n\\newcommand{\\colVec}[1]{% inline column vector\n\\bigl( \\begin{smallmatrix}#1\\end{smallmatrix}\\bigr)\n}\n\\newcommand{\\bigColVec}[1]{% inline column vector\n\\left( \\begin{matrix}#1\\end{matrix}\\right)\n}\n\\newcommand{\\state}[1]{{\\left| #1 \\right>}}H^{\\otimes n} \\left( \\left| 0 \\right>^{\\otimes n} \\right)= \\dfrac{1}{\\sqrt{2^n}} \\sum_{x=0}^{2^n-1} \\left|x \\right>$$\nThe quantum register may also include ancilla bits that must not necessarily be brought into superposition.\nFor example, if the last part of the quantum register is used for ancilla qubits, a different [Initialization](pattern-languages/af7780d5-1f97-4536-8da7-4194b093ab1d/312bc9d3-26c0-40ae-b90b-56effd136c0d) on the two parts of the register can be accomplished by using a tensor product operator $H^{\\otimes n}\\otimes U$ where the Hadamard transformation $H^{\\otimes n}$ only operates on the first part and $U$ operates on the ancilla qubits. If the ancilla qubits are not brought into superposition, $U$ is the Identity (e.g., $U=I^{\\otimes m}$):\n$$ \n\\newcommand{\\state}[1]{{\\left| #1 \\right>}}\nH^{\\otimes n} \\otimes I^{\\otimes m} \\left( \\left| 0 \\right>^{\\otimes n} \\otimes \\left| 0 \\right>^{\\otimes m} \\right)= \\frac{1}{\\sqrt{2^n}} \\sum_{x=0}^{2^n-1} \\left|x \\right> \\otimes \\state{0}^{\\otimes m}\n$$", "Variants": "", "Known Uses": "Countless quantum algorithms start with a  uniform superposition.\n\n[Grover''s Algorithm (PlanQK)](https://platform.planqk.de/algorithms/86dfd279-db46-4adb-84a7-39bcf1d19d3a/)\n[Drr-Hyer Quantum Minimization Algorithm (PlanQK)](https://platform.planqk.de/algorithms/061f4eb3-d9ee-4f47-befc-9e242bf801ce/)\n[Quantum Phase Estimation (PlanQK)](https://platform.planqk.de/algorithms/ba4c50e3-7429-4471-a250-1762ed4c556a/)\n[Quantum approximate optimization algorithm (PlanQK)](https://platform.planqk.de/algorithms/fae60bca-d2b6-4aa2-88b7-58caace34179/)\n\n", "Related Patterns": "[Uniform Superposition](pattern-languages/af7780d5-1f97-4536-8da7-4194b093ab1d/2229a430-fe92-4411-9d72-d10dd1d8da14) is a special case of [Initialization](pattern-languages/af7780d5-1f97-4536-8da7-4194b093ab1d/312bc9d3-26c0-40ae-b90b-56effd136c0d).  \nAfter a register is brought into uniform superposition, [Creating Entanglement](pattern-languages/af7780d5-1f97-4536-8da7-4194b093ab1d/3d1f3991-df47-4d42-8f9a-e6dcf4e3ccec) may be used. \nA [Function Table](pattern-languages/af7780d5-1f97-4536-8da7-4194b093ab1d/3f3fabf0-7fa7-4b43-a74a-46a7ac2c55ee)can be computed on a register in uniform superposition.\nAn [Oracle](pattern-languages/af7780d5-1f97-4536-8da7-4194b093ab1d/1cc7e9d6-ab37-412e-8afa-604a25de296e) may  require its input to be in uniform superposition."}', 'af7780d5-1f97-4536-8da7-4194b093ab1d', 'https://link.springer.com/chapter/10.1007/978-3-030-14082-3_19', false, false, 'circuit-level');
INSERT INTO public.pattern VALUES ('3e74edee-f076-45b3-8247-a5dd19cb24b2', 'Invalid Message Channel', 'https://patternpedia.org/patternLanguages/eip/invalidMessageChannel', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/InvalidMessageChannelIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/InvalidMessageSolution.gif)", "Solution": "The receiver should move the improper message to an Invalid Message Channel, a special channel for messages that could not be processed by their receivers.", "Driving Question": "How can a messaging receiver gracefully handle receiving a message that makes no sense?"}', 'https://www.enterpriseintegrationpatterns.com/img/InvalidMessageChannelIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('78185c7d-f353-49ee-b1e2-c2a0bcfc4ed2', 'Dead Letter Channel', 'https://patternpedia.org/patternLanguages/eip/deadLetterChannel', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/DeadLetterChannelIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/DeadLetterChannelSolution.gif)", "Solution": "When a messaging system determines that it cannot or should not deliver a message, it may elect to move the message to a Dead Letter Channel.", "Driving Question": "What will the messaging system do with a message it cannot deliver?"}', 'https://www.enterpriseintegrationpatterns.com/img/DeadLetterChannelIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('96ec8c86-c024-4d03-a283-49375e43e086', 'Guaranteed Delivery', 'https://patternpedia.org/patternLanguages/eip/guaranteedDelivery', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/GuaranteedMessagingIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/GuaranteedMessagingSolution.gif)", "Solution": "Use Guaranteed Delivery to make messages persistent so that they are not lost even if the messaging system crashes.", "Driving Question": "How can the sender make sure that a message will be delivered, even if the messaging system fails?"}', 'https://www.enterpriseintegrationpatterns.com/img/GuaranteedMessagingIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('b5c458ea-3a4e-4211-afc9-82510c2cff46', 'Channel Adapter', 'https://patternpedia.org/patternLanguages/eip/channelAdapter', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/ChannelAdapterIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/ChannelAdapterSolution.gif)", "Solution": "Use a Channel Adapter that can access the application''s API or data and publish messages on a channel based on this data, and that likewise can receive messages and invoke functionality inside the application.", "Driving Question": "How can you connect an application to the messaging system so that it can send and receive messages?"}', 'https://www.enterpriseintegrationpatterns.com/img/ChannelAdapterIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('adcaac7d-a028-4d33-9111-ef3e711637e7', 'Messaging Bridge', 'https://patternpedia.org/patternLanguages/eip/messagingBridge', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/MessagingBridgeIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/MessagingBridge.gif)", "Solution": "Use a Messaging Bridge, a connection between messaging systems, to replicate messages between systems.", "Driving Question": "How can multiple messaging systems be connected so that messages available on one are also available on the others?"}', 'https://www.enterpriseintegrationpatterns.com/img/MessagingBridgeIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('ccb661e2-6c0d-4020-9eb2-abd290230b5b', 'Message Bus', 'https://patternpedia.org/patternLanguages/eip/messageBus', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/MessageBusIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/MessageBusSolution.gif)", "Solution": "Structure the connecting middleware between these applications as a Message Bus that enables them to work together using messaging.", "Driving Question": "What is an architecture that enables separate applications to work together, but in a decoupled fashion such that applications can be easily added or removed without affecting the others?"}', 'https://www.enterpriseintegrationpatterns.com/img/MessageBusIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('712b29e2-411b-4ef8-8f81-6cae15222c07', 'Control Bus', 'https://patternpedia.org/patternLanguages/eip/controlBus', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/ControlBusIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/ControlBus.gif)", "Solution": "Use a Control Bus to manage an enterprise integration system. The Control Bus uses the same messaging mechanism used by the application data, but uses separate channels to transmit data that is relevant to the management of components involved in the message flow.", "Driving Question": "How can we effectively administer a messaging system that is distributed across multiple platforms and a wide geographic area?"}', 'https://www.enterpriseintegrationpatterns.com/img/ControlBusIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('662ffad9-7dd3-490f-93d2-efb92f74c5ae', 'Detour', 'https://patternpedia.org/patternLanguages/eip/detour', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/DetourIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/Detour.gif)", "Solution": "Construct a Detour with a context-based router controlled via the Control Bus. In one state the router routes incoming messages through additional steps while in the other it routes messages directly to the destination channel.", "Driving Question": "How can you route a message through intermediate steps to perform validation, testing or debugging functions?"}', 'https://www.enterpriseintegrationpatterns.com/img/DetourIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('b244a2fb-1f27-4e6d-8f43-7e512798f299', 'Wire Tap', 'https://patternpedia.org/patternLanguages/eip/wireTap', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/WireTapIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/WireTap.gif)", "Solution": "Insert a simple Recipient List into the channel that publishes each incoming message to the main channel and a secondary channel.", "Driving Question": "How do you inspect messages that travel on a point-to-point channel?"}', 'https://www.enterpriseintegrationpatterns.com/img/WireTapIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('84c81b22-765b-4edb-a6e7-c626151e0af8', 'Message History', 'https://patternpedia.org/patternLanguages/eip/messageHistory', '{"Icon": "", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/MessageHistory.gif)", "Solution": "Therefore, attach a Message History to the message. The Message History is a list of all applications that the message passed through since its origination.", "Driving Question": "How can we effectively analyze and debug the flow of messages in a loosely coupled system?"}', '', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('c699fdf0-f31e-45c0-9d0d-cee2388e1544', 'Smart Proxy', 'https://patternpedia.org/patternLanguages/eip/smartProxy', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/SmartProxyIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/SmartProxy.gif)", "Solution": "Use a Smart Proxy to store the Return Address supplied by the original requestor and replace it with the address of the Smart Proxy. When the service sends the reply message route it to the original Return Address.", "Driving Question": "How can you track messages on a service that publishes reply messages to the Return Address specified by the requestor?"}', 'https://www.enterpriseintegrationpatterns.com/img/SmartProxyIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('467cae27-d8be-4509-af26-64ed0a809028', 'Distributed Application', 'https://patternpedia.org/patternLanguages/cloudComputingPatterns/distributedApplication', '{"Icon": "Enter your input for this section here.", "Intent": "A cloud application divides provided functionality among multiple application components that can be scaled out independently.", "Solution Sketch": ""}', 'https://www.cloudcomputingpatterns.org/icons/distributed_application_icon.png', NULL, 'efdc1625-6445-4662-a37e-5f1fd37a542b', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('5778b4ba-4acd-4c94-9c10-d83d80f75d50', 'Test Message', 'https://patternpedia.org/patternLanguages/eip/testMessage', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/TestMessageIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/TestMessage.gif)", "Solution": "Therefore, use Test Message to assure the health of message processing components (see Figure).", "Driving Question": "What happens, though, if a component is actively processing messages, but garbles outgoing messages due to an internal fault?"}', 'https://www.enterpriseintegrationpatterns.com/img/TestMessageIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('38b1dd17-c3ff-46c4-b300-2e1c7d542abe', 'Channel Purger', 'https://patternpedia.org/patternLanguages/eip/channelPurger', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/ChannelPurgerIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/ChannelPurger.gif)", "Solution": "Use a Channel Purger to remove unwanted messages from a channel.", "Driving Question": "How can you keep ''left-over'' messages on a channel from disturbing tests or running systems?"}', 'https://www.enterpriseintegrationpatterns.com/img/ChannelPurgerIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('f619a8fd-fff8-4981-8947-48ebaaad993c', 'File Transfer', 'https://patternpedia.org/patternLanguages/eip/fileTransfer', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/FileTransferIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/FileTransferIntegration.gif) ", "Solution": "Have each application produce files containing information that other applications need to consume. Integrators take the responsibility of transforming files into different formats. Produce the files at regular intervals according to the nature of the business.", "Driving Question": "How can I integrate multiple applications so that they work together and can exchange information?"}', 'https://www.enterpriseintegrationpatterns.com/img/FileTransferIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('60e3ca01-0b7a-4785-acd7-3dddad0eea8e', 'Shared Database', 'https://patternpedia.org/patternLanguages/eip/sharedDatabase', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/SharedDatabaseIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/SharedDatabaseIntegration.gif)", "Solution": "Integrate applications by having them store their data in a single Shared Database.", "Driving Question": "How can I integrate multiple applications so that they work together and can exchange information?"}', 'https://www.enterpriseintegrationpatterns.com/img/SharedDatabaseIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('36531ec5-477c-4d74-8d84-43276359aa14', 'Remote Procedure Invocation', 'https://patternpedia.org/patternLanguages/eip/remoteProcedureInvocation', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/EncapsulatedSynchronousIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/EncapsulatedSynchronousIntegration.gif)", "Solution": "Develop each application as a large-scale object or component with encapsulated data. Provide an interface to allow other applications to interact with the running application.", "Driving Question": "How can I integrate multiple applications so that they work together and can exchange information?"}', 'https://www.enterpriseintegrationpatterns.com/img/EncapsulatedSynchronousIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('a7e45f3a-e408-4593-9aba-0a724c5ebf26', 'Messaging', 'https://patternpedia.org/patternLanguages/eip/messaging', '{"Icon": "![](https://www.enterpriseintegrationpatterns.com/img/MessagingIcon.gif)", "Sketch": "![](https://www.enterpriseintegrationpatterns.com/img/Messaging.gif)", "Solution": "Use Messaging to transfer packets of data frequently, immediately, reliably, and asynchronously, using customizable formats.", "Driving Question": "How can I integrate multiple applications so that they work together and can exchange information?"}', 'https://www.enterpriseintegrationpatterns.com/img/MessagingIcon.gif', NULL, 'f6b807aa-5905-4435-aec6-324355a0483f', NULL, false, false, NULL);
