INSERT INTO public.pattern VALUES ('61130a8b-68ad-4281-80ad-a62d44bc4090', 'Hybrid Cloud', 'https://patternpedia.org/patternLanguages/cloudComputingPatterns/hybridCloud', '{"Icon": "![](http://www.cloudcomputingpatterns.org/icons/hybrid_cloud_icon.png)", "Intent": "Different clouds and static data centers are integrated to form a homogeneous hosting environment.", "Solution Sketch": "![](http://www.cloudcomputingpatterns.org/sketches/hybrid_cloud_sketch.png)"}', 'http://www.cloudcomputingpatterns.org/icons/hybrid_cloud_icon.png', NULL, 'efdc1625-6445-4662-a37e-5f1fd37a542b', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('386a4d90-1a3d-427c-96a6-6f1a433daae6', 'Virtual Networking', 'https://patternpedia.org/patternLanguages/cloudComputingPatterns/virtualNetworking', '{"Icon": "![](http://www.cloudcomputingpatterns.org/icons/virtual_networking_icon.png)", "Intent": "Networking resources are virtualized to empower customers to configure networks, firewalls, and remote access using a self-service interface.", "Solution Sketch": "![](http://www.cloudcomputingpatterns.org/sketches/virtual_networking_sketch.png)"}', 'http://www.cloudcomputingpatterns.org/icons/virtual_networking_icon.png', NULL, 'efdc1625-6445-4662-a37e-5f1fd37a542b', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('2fb586fd-9982-4f4e-987b-17909069e10e', 'Hybrid Backup', 'https://patternpedia.org/patternLanguages/cloudComputingPatterns/hybridBackup', '{"Icon": "![](https://www.cloudcomputingpatterns.org/icons/hybrid_backup_icon.png)", "Intent": "Data is periodically extracted from an application to be archived in an elastic cloud for disaster recovery purposes.", "Solution Sketch": "![](https://www.cloudcomputingpatterns.org/sketches/hybrid_backup_sketch.png)"}', 'https://www.cloudcomputingpatterns.org/icons/hybrid_backup_icon.png', NULL, 'efdc1625-6445-4662-a37e-5f1fd37a542b', NULL, false, false, NULL);
