INSERT INTO public.pattern VALUES ('7c8849ee-26ad-4910-b798-902367e617d6', 'Node-based Availability', 'https://patternpedia.org/patternLanguages/cloudComputingPatterns/node-basedAvailability', '{"Icon": "![](http://www.cloudcomputingpatterns.org/icons/node_based_availability_icon.png)", "Intent": "A cloud provider guarantees the availability of individual nodes, such as individual virtual servers, middleware components or hosted application components.", "Solution Sketch": "![](http://www.cloudcomputingpatterns.org/sketches/node_based_availability_sketch.png)"}', 'http://www.cloudcomputingpatterns.org/icons/node_based_availability_icon.png', NULL, 'efdc1625-6445-4662-a37e-5f1fd37a542b', NULL, false, false, NULL);
INSERT INTO public.pattern VALUES ('55f886b3-4fe0-4bc8-8bde-d0b7004a70ee', 'Loose Coupling', 'https://patternpedia.org/patternLanguages/cloudComputingPatterns/looseCoupling', '{"Icon": "![](https://www.cloudcomputingpatterns.org/icons/loose_coupling_icon.png)", "Intent": "A communication intermediary separates application functionality from concerns of communication partners regarding their location, implementation platform, the time of communication, and the used data format.", "Solution Sketch": "![](https://www.cloudcomputingpatterns.org/sketches/loose_coupling_sketch.png)"}', 'https://www.cloudcomputingpatterns.org/icons/loose_coupling_icon.png', NULL, 'efdc1625-6445-4662-a37e-5f1fd37a542b', NULL, false, false, NULL);
