INSERT INTO public.directed_edge VALUES ('46ecfadb-6110-46df-82dc-a217d546628f', '"Quantum Modules and Quantum Module Templates can be used to build larger quantum circuits from small modules, provided they are compatible with each other."', 'canBeUsedWith', 'af7780d5-1f97-4536-8da7-4194b093ab1d', 'd5cda8c9-f39b-474a-9032-c17131884975', '424c803a-aefb-4b54-8e45-d177fedac893');
